mapper.update:
UPDATE ${db.schema}."ProductSalesPoint" SET
product_price = #{productPrice},
product_quantity = #{productQuantity} 
WHERE product_barcode = #{productBarcode} AND sales_point_id = #{salesPointId};

mapper.select:
SELECT * 
FROM ${db.schema}."ProductSalesPoint" 
WHERE product_barcode  =  #{productBarcode} 
	  AND sales_point_id = #{salesPointId};

mapper.delete:
DELETE FROM ${db.schema}."ProductSalesPoint" 
WHERE product_barcode  =  #{productBarcode} 
	  AND sales_point_id = #{salesPointId};
