mapper.select:
SELECT * 
FROM ${db.schema}."Product" 
WHERE (product_name ILIKE #{value} 
OR product_trade_mark ILIKE #{value}
OR product_type IN (SELECT type_id FROM ${db.schema}."TypeKeyword" WHERE keyword ILIKE #{value})
AND product_trade_mark ILIKE #{tradeMark}
AND product_type = #{type}
)
;

mapper.update:
UPDATE
${db.schema}."Product" SET

product_name = #{productName},
product_type = #{productType},
product_trade_mark =#{productTradeMark}

WHERE product_barcode = #{productBarcode};

mapper.select:
		 SELECT p.product_name  FROM  ${db.schema}."Product" p 
		 WHERE p.product_name ILIKE #{query} 
		 UNION
		 SELECT k.keyword FROM ${db.schema}."TypeKeyword" k 
         WHERE k.keyword ILIKE #{query};
