mapper.select:
SELECT * FROM ${db.schema}."Product" WHERE (product_name ILIKE
#{value} OR product_trade_mark ILIKE #{value})
AND product_trade_mark ILIKE #{tradeMark}
AND product_type = #{type}
;

mapper.update:
UPDATE ${db.schema}."Product" SET
product_name = #{productName}, product_type = #{productType}, product_category = #{productCategory}, product_trade_mark =#{productTradeMark}
WHERE product_barcode = #{productBarcode};

mapper.select:
		SELECT type_caracteristic_name, product_caracteristic_value 	    
        FROM ${db.schema}."Product" p, ${db.schema}."TypeCaracteristic" tcar , ${db.schema}."ProductCaracteristic" pcar
		  WHERE p.product_barcode = #{productBarcode} 
		  AND tcar.type_id = p.product_type
		  AND pcar.type_caracteristic_id = tcar.type_caracteristic_id
		  AND pcar.product_barcode = p.product_barcode;
